-- a procedure on coders

drop procedure if exists get_coder_salary;

DELIMITER //

CREATE PROCEDURE get_coder_salary(
	in p_coder_id integer,
    out p_salary decimal(8, 2)
)
begin
	select salary
	into p_salary
	from coders
	where coder_id = p_coder_id;
end;

